        guard result == SQLITE_OK else {
            throw DatabaseError.failedToExecute("Failed to prepare PRAGMA \(pragma)")
        }

        guard sqlite3_step(statement) == SQLITE_ROW else {
            return nil
        }

        return sqlite3_column_int(statement, 0)
    }

