// SPDX-License-Identifier: MIT
// SchemaManager.swift - Schema versioning and forward-only migrations

import Foundation

/// Represents a single database migration
public struct Migration {
    /// The version this migration upgrades to
    public let version: Int32

    /// Human-readable description of the migration
    public let description: String

    /// SQL statements to execute for this migration
    public let statements: [String]

    public init(version: Int32, description: String, statements: [String]) {
        self.version = version
        self.description = description
        self.statements = statements
    }
}

/// Manages database schema versioning and migrations.
/// Implements forward-only migration system per SPEC.md Section 6.3.
public final class SchemaManager {

    private let connection: DatabaseConnection

    /// All registered migrations in version order
    private var migrations: [Migration] = []

    /// Creates a new schema manager
    /// - Parameter connection: An open database connection
    public init(connection: DatabaseConnection) {
        self.connection = connection
        registerMigrations()
    }

    /// Registers all known migrations
    private func registerMigrations() {
        // Version 1: Initial schema
        migrations.append(Migration(
            version: 1,
            description: "Initial schema with all tables, indexes, and immutability triggers",
            statements: Schema.allStatements
        ))

        // Future migrations would be added here:
        // migrations.append(Migration(version: 2, description: "...", statements: [...]))
    }

    /// Gets the current schema version from the database
    public func getCurrentVersion() throws -> Int32 {
        try connection.getSchemaVersion()
    }

    /// Initializes a new database with the current schema
    /// - Throws: DatabaseError if initialization fails
    public func initializeSchema() throws {
        let currentVersion = try getCurrentVersion()

        if currentVersion == 0 {
            // Fresh database - apply all statements directly
            try applyInitialSchema()
        } else if currentVersion < Schema.currentVersion {
            // Existing database - run migrations
            try runMigrations(from: currentVersion)
        } else if currentVersion > Schema.currentVersion {
            throw DatabaseError.schemaVersionMismatch(
                expected: Schema.currentVersion,
                actual: currentVersion
            )
        }
        // If currentVersion == Schema.currentVersion, nothing to do
    }

    /// Applies the initial schema to a fresh database
    private func applyInitialSchema() throws {
        // Execute all DDL statements
        for statement in Schema.allStatements {
            try connection.execute(statement)
        }

        // Record the migration
        try recordMigration(version: Schema.currentVersion, description: "Initial schema")

        // Set the schema version
        try connection.setSchemaVersion(Schema.currentVersion)
    }

    /// Runs all pending migrations from the given version
    private func runMigrations(from startVersion: Int32) throws {
        let pendingMigrations = migrations.filter { $0.version > startVersion }
            .sorted { $0.version < $1.version }

        for migration in pendingMigrations {
            try applyMigration(migration)
        }
    }

    /// Applies a single migration
    private func applyMigration(_ migration: Migration) throws {
        // Execute all statements in the migration
        for statement in migration.statements {
            do {
                try connection.execute(statement)
            } catch {
                throw DatabaseError.migrationFailed(
                    version: migration.version,
                    reason: "Failed to execute: \(statement). Error: \(error)"
                )
            }
        }

        // Record the migration
        try recordMigration(version: migration.version, description: migration.description)

        // Update the schema version
        try connection.setSchemaVersion(migration.version)
    }

    /// Records a migration in the schema_migrations table
    private func recordMigration(version: Int32, description: String) throws {
        let nowMicroseconds = Int64(Date().timeIntervalSince1970 * 1_000_000)
        let escapedDescription = description.replacingOccurrences(of: "'", with: "''")

        let sql = """
            INSERT INTO schema_migrations (version, applied_ts_us, description)
            VALUES (\(version), \(nowMicroseconds), '\(escapedDescription)');
            """

        try connection.execute(sql)
    }

    /// Checks if the schema is up to date
    public func isSchemaUpToDate() throws -> Bool {
        try getCurrentVersion() == Schema.currentVersion
    }

    /// Returns a list of applied migrations from the database
    public func getAppliedMigrations() throws -> [(version: Int32, appliedAt: Int64, description: String?)] {
        // This would require a query method on DatabaseConnection
        // For now, we just verify the version
        let version = try getCurrentVersion()
        if version > 0 {
            return [(version: version, appliedAt: 0, description: nil)]
        }
        return []
    }
}

// SPDX-License-Identifier: MIT
// Storage.swift - Public API for the Storage module

import Foundation

/// Storage module for well-whaddya-know.
/// Provides SQLite database management with:
/// - Schema initialization and migrations
/// - Connection configuration with required PRAGMAs
/// - Immutability enforcement via triggers
///
/// Usage:
/// ```swift
/// let connection = DatabaseConnection(path: "/path/to/wwk.sqlite")
/// try connection.open()
/// let schemaManager = SchemaManager(connection: connection)
/// try schemaManager.initializeSchema()
/// ```
public enum Storage {
    /// Current schema version
    public static var schemaVersion: Int32 { Schema.currentVersion }
}

// SPDX-License-Identifier: MIT
// StorageTests.swift - Unit tests for the Storage module

import XCTest
@testable import Storage

final class StorageTests: XCTestCase {

    var connection: DatabaseConnection!
    var schemaManager: SchemaManager!

    override func setUp() {
        super.setUp()
        // Use in-memory database for tests
        connection = DatabaseConnection(path: ":memory:")
        try! connection.open()
        schemaManager = SchemaManager(connection: connection)
    }

    override func tearDown() {
        connection.close()
        connection = nil
        schemaManager = nil
        super.tearDown()
    }

    // MARK: - Schema Initialization Tests

    func testSchemaInitializationFromScratch() throws {
        // Given: A fresh database with version 0
        let initialVersion = try connection.getSchemaVersion()
        XCTAssertEqual(initialVersion, 0, "Fresh database should have version 0")

        // When: We initialize the schema
        try schemaManager.initializeSchema()

        // Then: The schema version should be set to current
        let finalVersion = try connection.getSchemaVersion()
        XCTAssertEqual(finalVersion, Schema.currentVersion)
    }

    func testSchemaInitializationCreatesAllTables() throws {
        // When: We initialize the schema
        try schemaManager.initializeSchema()

        // Then: All expected tables should exist
        let expectedTables = [
            "identity",
            "kv_metadata",
            "applications",
            "window_titles",
            "tags",
            "agent_runs",
            "system_state_events",
            "raw_activity_events",
            "user_edit_events",
            "schema_migrations"
        ]

        for tableName in expectedTables {
            XCTAssertTrue(try tableExists(tableName), "Table '\(tableName)' should exist")
        }
    }

    func testSchemaInitializationIsIdempotent() throws {
        // When: We initialize the schema twice
        try schemaManager.initializeSchema()
        try schemaManager.initializeSchema()

        // Then: No error should occur and version should be correct
        let version = try connection.getSchemaVersion()
        XCTAssertEqual(version, Schema.currentVersion)
    }

    // MARK: - PRAGMA Configuration Tests

    func testForeignKeysEnabled() throws {
        // Given: An open connection
        // When: We check the foreign_keys pragma
        let value = try connection.getPragmaIntValue("foreign_keys")

        // Then: It should be enabled (1)
        XCTAssertEqual(value, 1, "foreign_keys should be ON")
    }

    func testJournalModeIsWAL() throws {
        let value = try connection.getPragmaValue("journal_mode")
        XCTAssertEqual(value?.lowercased(), "wal", "journal_mode should be WAL")
    }

    func testSynchronousIsNormal() throws {
        let value = try connection.getPragmaIntValue("synchronous")
        // NORMAL = 1
        XCTAssertEqual(value, 1, "synchronous should be NORMAL (1)")
    }

    func testBusyTimeoutIsSet() throws {
        let value = try connection.getPragmaIntValue("busy_timeout")
        XCTAssertEqual(value, 5000, "busy_timeout should be 5000")
    }

    func testTempStoreIsMemory() throws {
        let value = try connection.getPragmaIntValue("temp_store")
        // MEMORY = 2
        XCTAssertEqual(value, 2, "temp_store should be MEMORY (2)")
    }

    func testVerifyPragmasReturnsAllSettings() throws {
        let pragmas = try connection.verifyPragmas()

        XCTAssertNotNil(pragmas["foreign_keys"])
        XCTAssertNotNil(pragmas["journal_mode"])
        XCTAssertNotNil(pragmas["synchronous"])
        XCTAssertNotNil(pragmas["busy_timeout"])
        XCTAssertNotNil(pragmas["temp_store"])
    }

    // MARK: - Helper Methods

    private func tableExists(_ name: String) throws -> Bool {
        guard let db = connection.rawPointer else { return false }

        var statement: OpaquePointer?
        defer { sqlite3_finalize(statement) }

        let sql = "SELECT name FROM sqlite_master WHERE type='table' AND name=?;"
        guard sqlite3_prepare_v2(db, sql, -1, &statement, nil) == SQLITE_OK else {
            return false
        }

        sqlite3_bind_text(statement, 1, name, -1, nil)
        return sqlite3_step(statement) == SQLITE_ROW
    }
}

// SPDX-License-Identifier: MIT
// ImmutabilityTests.swift - Tests for immutability triggers on event tables

import XCTest
import SQLite3
@testable import Storage

/// Tests that immutability triggers correctly prevent UPDATE and DELETE operations
/// on system_state_events, raw_activity_events, and user_edit_events tables.
final class ImmutabilityTests: XCTestCase {

    var connection: DatabaseConnection!
    var schemaManager: SchemaManager!

    override func setUp() {
        super.setUp()
        connection = DatabaseConnection(path: ":memory:")
        try! connection.open()
        schemaManager = SchemaManager(connection: connection)
        try! schemaManager.initializeSchema()

        // Insert prerequisite data for foreign key constraints
        try! insertPrerequisiteData()
    }

    override func tearDown() {
        connection.close()
        connection = nil
        schemaManager = nil
        super.tearDown()
    }

    /// Inserts data required by foreign key constraints
    private func insertPrerequisiteData() throws {
        // Insert an agent_run for foreign key references
        try connection.execute("""
            INSERT INTO agent_runs (run_id, started_ts_us, started_monotonic_ns, agent_version, os_version)
            VALUES ('test-run-1', 1000000, 1000000000, '1.0.0', '14.0');
            """)

        // Insert an application for raw_activity_events
        try connection.execute("""
            INSERT INTO applications (app_id, bundle_id, display_name, first_seen_ts_us)
            VALUES (1, 'com.test.app', 'Test App', 1000000);
            """)

        // Insert a tag for user_edit_events
        try connection.execute("""
            INSERT INTO tags (tag_id, name, created_ts_us, sort_order)
            VALUES (1, 'test-tag', 1000000, 0);
            """)
    }

    // MARK: - system_state_events Immutability Tests

    func testSystemStateEventsRejectsUpdate() throws {
        // Given: A row in system_state_events
        try connection.execute("""
            INSERT INTO system_state_events (
                run_id, event_ts_us, event_monotonic_ns,
                is_system_awake, is_session_on_console, is_screen_locked, is_working,
                event_kind, source, tz_identifier, tz_offset_seconds
            ) VALUES (
                'test-run-1', 1000000, 1000000000,
                1, 1, 0, 1,
                'agent_start', 'startup_probe', 'America/Los_Angeles', -28800
            );
            """)

        // When: We attempt to UPDATE the row
        // Then: The trigger should abort with an error
        XCTAssertThrowsError(try connection.execute("""
            UPDATE system_state_events SET is_working = 0 WHERE sse_id = 1;
            """)) { error in
            guard case DatabaseError.failedToExecute(let message) = error else {
                XCTFail("Expected DatabaseError.failedToExecute, got \(error)")
                return
            }
            XCTAssertTrue(message.contains("immutable"), "Error should mention immutability: \(message)")
        }
    }

    func testSystemStateEventsRejectsDelete() throws {
        // Given: A row in system_state_events
        try connection.execute("""
            INSERT INTO system_state_events (
                run_id, event_ts_us, event_monotonic_ns,
                is_system_awake, is_session_on_console, is_screen_locked, is_working,
                event_kind, source, tz_identifier, tz_offset_seconds
            ) VALUES (
                'test-run-1', 1000000, 1000000000,
                1, 1, 0, 1,
                'agent_start', 'startup_probe', 'America/Los_Angeles', -28800
            );
            """)

        // When: We attempt to DELETE the row
        // Then: The trigger should abort with an error
        XCTAssertThrowsError(try connection.execute("""
            DELETE FROM system_state_events WHERE sse_id = 1;
            """)) { error in
            guard case DatabaseError.failedToExecute(let message) = error else {
                XCTFail("Expected DatabaseError.failedToExecute, got \(error)")
                return
            }
            XCTAssertTrue(message.contains("immutable"), "Error should mention immutability: \(message)")
        }
    }

    // MARK: - raw_activity_events Immutability Tests

    func testRawActivityEventsRejectsUpdate() throws {
        // Given: A row in raw_activity_events
        try connection.execute("""
            INSERT INTO raw_activity_events (
                run_id, event_ts_us, event_monotonic_ns,
                app_id, pid, title_status, reason, is_working
            ) VALUES (
                'test-run-1', 1000000, 1000000000,
                1, 12345, 'ok', 'working_began', 1
            );
            """)

        // When: We attempt to UPDATE the row
        // Then: The trigger should abort with an error
        XCTAssertThrowsError(try connection.execute("""
            UPDATE raw_activity_events SET is_working = 0 WHERE rae_id = 1;
            """)) { error in
            guard case DatabaseError.failedToExecute(let message) = error else {
                XCTFail("Expected DatabaseError.failedToExecute, got \(error)")
                return
            }
            XCTAssertTrue(message.contains("immutable"), "Error should mention immutability: \(message)")
        }
    }

    func testRawActivityEventsRejectsDelete() throws {
        // Given: A row in raw_activity_events
        try connection.execute("""
            INSERT INTO raw_activity_events (
                run_id, event_ts_us, event_monotonic_ns,
                app_id, pid, title_status, reason, is_working
            ) VALUES (
                'test-run-1', 1000000, 1000000000,
                1, 12345, 'ok', 'working_began', 1
            );
            """)

        // When: We attempt to DELETE the row
        // Then: The trigger should abort with an error
        XCTAssertThrowsError(try connection.execute("""
            DELETE FROM raw_activity_events WHERE rae_id = 1;
            """)) { error in
            guard case DatabaseError.failedToExecute(let message) = error else {
                XCTFail("Expected DatabaseError.failedToExecute, got \(error)")
                return
            }
            XCTAssertTrue(message.contains("immutable"), "Error should mention immutability: \(message)")
        }
    }
}

