// StorageTests.swift - Unit tests for the Storage module

import XCTest
@testable import Storage

final class StorageTests: XCTestCase {

    var connection: DatabaseConnection!
    var schemaManager: SchemaManager!

    override func setUp() {
        super.setUp()
        // Use in-memory database for tests
        connection = DatabaseConnection(path: ":memory:")
        try! connection.open()
        schemaManager = SchemaManager(connection: connection)
    }

    override func tearDown() {
        connection.close()
        connection = nil
        schemaManager = nil
        super.tearDown()
    }

    // MARK: - Schema Initialization Tests

    func testSchemaInitializationFromScratch() throws {
        // Given: A fresh database with version 0
        let initialVersion = try connection.getSchemaVersion()
        XCTAssertEqual(initialVersion, 0, "Fresh database should have version 0")

        // When: We initialize the schema
        try schemaManager.initializeSchema()

        // Then: The schema version should be set to current
        let finalVersion = try connection.getSchemaVersion()
        XCTAssertEqual(finalVersion, Schema.currentVersion)
    }

    func testSchemaInitializationCreatesAllTables() throws {
        // When: We initialize the schema
        try schemaManager.initializeSchema()

        // Then: All expected tables should exist
        let expectedTables = [
            "identity",
            "kv_metadata",
            "applications",
            "window_titles",
            "tags",
            "agent_runs",
            "system_state_events",
            "raw_activity_events",
            "user_edit_events",
            "schema_migrations"
        ]

        for tableName in expectedTables {
            XCTAssertTrue(try tableExists(tableName), "Table '\(tableName)' should exist")
        }
    }

    func testSchemaInitializationIsIdempotent() throws {
        // When: We initialize the schema twice
        try schemaManager.initializeSchema()
        try schemaManager.initializeSchema()

        // Then: No error should occur and version should be correct
        let version = try connection.getSchemaVersion()
        XCTAssertEqual(version, Schema.currentVersion)
    }

    // MARK: - PRAGMA Configuration Tests

    func testForeignKeysEnabled() throws {
        // Given: An open connection
        // When: We check the foreign_keys pragma
        let value = try connection.getPragmaIntValue("foreign_keys")

        // Then: It should be enabled (1)
        XCTAssertEqual(value, 1, "foreign_keys should be ON")
    }

    func testJournalModeIsWAL() throws {
        let value = try connection.getPragmaValue("journal_mode")
        XCTAssertEqual(value?.lowercased(), "wal", "journal_mode should be WAL")
    }

    func testSynchronousIsNormal() throws {
        let value = try connection.getPragmaIntValue("synchronous")
        // NORMAL = 1
        XCTAssertEqual(value, 1, "synchronous should be NORMAL (1)")
    }

    func testBusyTimeoutIsSet() throws {
        let value = try connection.getPragmaIntValue("busy_timeout")
        XCTAssertEqual(value, 5000, "busy_timeout should be 5000")
    }

    func testTempStoreIsMemory() throws {
        let value = try connection.getPragmaIntValue("temp_store")
        // MEMORY = 2
        XCTAssertEqual(value, 2, "temp_store should be MEMORY (2)")
    }

    func testVerifyPragmasReturnsAllSettings() throws {
        let pragmas = try connection.verifyPragmas()

        XCTAssertNotNil(pragmas["foreign_keys"])
        XCTAssertNotNil(pragmas["journal_mode"])
        XCTAssertNotNil(pragmas["synchronous"])
        XCTAssertNotNil(pragmas["busy_timeout"])
        XCTAssertNotNil(pragmas["temp_store"])
    }

    // MARK: - Helper Methods

    private func tableExists(_ name: String) throws -> Bool {
        guard let db = connection.rawPointer else { return false }

        var statement: OpaquePointer?
        defer { sqlite3_finalize(statement) }

        let sql = "SELECT name FROM sqlite_master WHERE type='table' AND name=?;"
        guard sqlite3_prepare_v2(db, sql, -1, &statement, nil) == SQLITE_OK else {
            return false
        }

        sqlite3_bind_text(statement, 1, name, -1, nil)
        return sqlite3_step(statement) == SQLITE_ROW
    }
}
"}

