import XCTest
import SQLite3
@testable import Storage

/// Tests for schema versioning and migration paths.
final class MigrationTests: XCTestCase {

    var connection: DatabaseConnection!

    override func setUp() {
        super.setUp()
        connection = DatabaseConnection(path: ":memory:")
        try! connection.open()
    }

    override func tearDown() {
        connection.close()
        connection = nil
        super.tearDown()
    }

    // MARK: - Schema Version Tests

    func testFreshDatabaseHasVersionZero() throws {
        let version = try connection.getSchemaVersion()
        XCTAssertEqual(version, 0, "Fresh database should have version 0")
    }

    func testSchemaVersionCanBeSet() throws {
        try connection.setSchemaVersion(42)
        let version = try connection.getSchemaVersion()
        XCTAssertEqual(version, 42)
    }

    func testSchemaVersionPersistsAcrossQueries() throws {
        try connection.setSchemaVersion(5)

        // Execute some other queries
        try connection.execute("SELECT 1;")
        try connection.execute("SELECT 2;")

        let version = try connection.getSchemaVersion()
        XCTAssertEqual(version, 5)
    }

    // MARK: - Migration System Tests

    func testInitializeSchemaFromScratch() throws {
        let schemaManager = SchemaManager(connection: connection)

        // Before initialization
        XCTAssertEqual(try connection.getSchemaVersion(), 0)

        // Initialize
        try schemaManager.initializeSchema()

        // After initialization
        XCTAssertEqual(try connection.getSchemaVersion(), Schema.currentVersion)
    }

    func testMigrationRecordedInTable() throws {
        let schemaManager = SchemaManager(connection: connection)
        try schemaManager.initializeSchema()

        // Check that migration was recorded
        guard let db = connection.rawPointer else {
            XCTFail("Database not open")
            return
        }

        var statement: OpaquePointer?
        defer { sqlite3_finalize(statement) }

        let sql = "SELECT version, description FROM schema_migrations WHERE version = ?;"
        guard sqlite3_prepare_v2(db, sql, -1, &statement, nil) == SQLITE_OK else {
            XCTFail("Failed to prepare statement")
            return
        }

        sqlite3_bind_int(statement, 1, Schema.currentVersion)

        XCTAssertEqual(sqlite3_step(statement), SQLITE_ROW, "Migration record should exist")

        let version = sqlite3_column_int(statement, 0)
        XCTAssertEqual(version, Schema.currentVersion)
    }

    func testIsSchemaUpToDate() throws {
        let schemaManager = SchemaManager(connection: connection)

        // Before initialization
        XCTAssertFalse(try schemaManager.isSchemaUpToDate())

        // After initialization
        try schemaManager.initializeSchema()
        XCTAssertTrue(try schemaManager.isSchemaUpToDate())
    }

    func testSchemaVersionMismatchThrows() throws {
        // Set a version higher than current
        try connection.setSchemaVersion(Schema.currentVersion + 10)

        let schemaManager = SchemaManager(connection: connection)

        XCTAssertThrowsError(try schemaManager.initializeSchema()) { error in
            guard case DatabaseError.schemaVersionMismatch(let expected, let actual) = error else {
                XCTFail("Expected schemaVersionMismatch error, got \(error)")
                return
            }
            XCTAssertEqual(expected, Schema.currentVersion)
            XCTAssertEqual(actual, Schema.currentVersion + 10)
        }
    }

    func testMultipleInitializationsAreSafe() throws {
        let schemaManager = SchemaManager(connection: connection)

        // Initialize multiple times
        try schemaManager.initializeSchema()
        try schemaManager.initializeSchema()
        try schemaManager.initializeSchema()

        // Should still be at current version
        XCTAssertEqual(try connection.getSchemaVersion(), Schema.currentVersion)
    }

    // MARK: - Schema Constants Tests

    func testSchemaCurrentVersionIsPositive() {
        XCTAssertGreaterThan(Schema.currentVersion, 0, "Schema version should be positive")
    }

    func testAllStatementsNotEmpty() {
        XCTAssertFalse(Schema.allStatements.isEmpty, "Should have DDL statements")
        XCTAssertFalse(Schema.allTableStatements.isEmpty, "Should have table statements")
        XCTAssertFalse(Schema.allIndexStatements.isEmpty, "Should have index statements")
        XCTAssertFalse(Schema.allTriggerStatements.isEmpty, "Should have trigger statements")
    }

    func testAllStatementsAreValidSQL() throws {
        // Each statement should be valid SQL (parseable)
        for statement in Schema.allStatements {
            XCTAssertNoThrow(try connection.execute(statement), "Statement should be valid: \(statement.prefix(50))...")
        }
    }
